CREATE TABLE `catalogos` (
  `idCatalogos` int(11) NOT NULL AUTO_INCREMENT,
  `Catalogo_titulo` varchar(100) NOT NULL,
  `Catalogo_sinopse` longtext NOT NULL,
  `Catalogo_img` longtext DEFAULT NULL,
  `usuarios_idUsuarios` int(11) NOT NULL,
  `Catalogo_link` longtext DEFAULT NULL,
  `Catalogo_autor` varchar(100) DEFAULT NULL,
  `Catalogo_disponivel_biblioteca` enum('1','2') DEFAULT NULL,
  PRIMARY KEY (`idCatalogos`),
  KEY `fk_Catalogos_usuarios_idx` (`usuarios_idUsuarios`),
  CONSTRAINT `fk_Catalogos_usuarios` FOREIGN KEY (`usuarios_idUsuarios`) REFERENCES `usuarios` (`idUsuarios`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;
