--
-- Table structure for table `conteudos`
--

DROP TABLE IF EXISTS `conteudos`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `conteudos` (
  `idConteudos` int(11) NOT NULL AUTO_INCREMENT,
  `Conteudo_titulo` varchar(50) NOT NULL,
  `Conteudo_img` longtext DEFAULT NULL,
  `Conteudo_cont` longtext DEFAULT NULL,
  `Conteudo_tipo` enum('1','2') NOT NULL,
  `Usuarios_idUsuarios` int(11) NOT NULL,
  `Matéria_idMatéria` int(11) NOT NULL,
  `Conteudo_dica` longtext DEFAULT NULL,
  `Conteudo_status` enum('1','2','3') NOT NULL,
  `Conteudo_autor` varchar(100) DEFAULT NULL,
  `Conteudo_link` longtext DEFAULT NULL,
  PRIMARY KEY (`idConteudos`),
  KEY `fk_Noticias_Usuarios_idx` (`Usuarios_idUsuarios`),
  KEY `fk_Noticias_Matéria1_idx` (`Matéria_idMatéria`),
  CONSTRAINT `fk_Noticias_Matéria1` FOREIGN KEY (`Matéria_idMatéria`) REFERENCES `matéria` (`idMatéria`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `fk_Noticias_Usuarios` FOREIGN KEY (`Usuarios_idUsuarios`) REFERENCES `usuarios` (`idUsuarios`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=28 DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `conteudos`
